import React from 'react';
import ReactDOM from 'react-dom';
import history from '../history';

const Modal = (props) => {
	return ReactDOM.createPortal(
		<div onClick={() => history.push('/')} className="ui dimmer modals visible active">
			<div onClick={(event) => event.stopPropagation()} className="ui standard modal visible active">
				<div className="header">Delete Stream</div>
				<div className="content">Are you sure you want to delete this stream?</div>
				<div className="actions">
					<button className="ui primary button">Delete</button>
					<button className="ui button">Cancel</button>
				</div>
			</div>
		</div>,
		document.querySelector('#modal') /* In this way we don't render the modal directly in our root html file which will cause our rest of the code
										  * to be overwritten, but we render it in a sibling div of the root element */
	);
};

export default Modal;
