import React from 'react';
import { Field, reduxForm } from 'redux-form';

class StreamCreate extends React.Component {

	renderError = (meta) => {
		if(meta.touched && meta.error) {
			return(
				<div className="ui error message">
					<div className="header">
					{meta.error}
					</div>
				</div>
			);
		}
	}


	renderInput = (formProps) => {
		const className = `field ${fromProps.meta.error && meta.touched ? 'error' : ''}`;

		return(
			<div className={className}>
				<label>{formProps.label}</label>
				<input {...formProps.input} autoComplete="off" />
				{this.renderError(formProps.meta)}
			</div>
		);
	}


	onSubmit(formValues) {
		alert("nanana");
	}


	render() {
		return(
			<form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error">
				<Field name="title" component={this.renderInput} label="Enter title" />
				<Field name="description" component={this.renderInput} label="Enter description" />
				<button className="ui button primary">Submit</button>
			</form>
		);
	}
}


// called when the form is initally rendered or useri nteracts with it
const validate = (formValues) => {
	const errors = {};

	if(!formValues.title) {
		// the user did not enter a title
		errors.title = 'You must enter a title';
	}

	if(!formValues.description) {
		// the user did not enter a title
		errors.description = 'You must enter a description';
	}

	return errors;
};


export default reduxForm({
	form: 'streamCreate',
	validate: validate // MANDATORY to let the validate function to run when it should
})(StreamCreate);
